$(function(){$(".ethiopian_date_picker").calendarsPicker($.extend({calendar:$.calendars.instance("ethiopian","am"),dateFormat:"dd/mm/yyyy",maxDate:"1y"})),$("#admission_admission_type").change(function(){var i=$(this).val(),a=$("#admission_procedure_category_id").val();$.ajax({url:"/admissions/load_sub_form",data:{admission_type:i,category:a},success:function(i){$("#sub_admission_form").html(i)}})}),$("#admission_admission_type").change(function(){var i=$(this).val(),a=$("#admission_procedure_category_id").val();$.ajax({url:"/admissions/load_sub_form",data:{admission_type:i,category:a},success:function(i){$("#sub_admission_form").html(i)}})}),$("#admission_listing_status, #admission_priority").change(function(){var i=$(this).val(),a=$("#admission_procedure_id").val(),s=$("#admission_priority").is(":checked");$.ajax({url:"/admissions/load_information",data:{listing_status:i,procedure:a,priority:s},success:function(i){$("#information_display").html(i)}})}),$("#admission_procedure_id").change(function(){var i=$("#admission_procedure_id").val();$.ajax({url:"/admissions/load_category",data:{procedure:i},success:function(i){$("#procedure_category_display").html(i)}})}),$("#admission_procedure_id").change(function(){var i=$("#admission_procedure_id").val();$.ajax({url:"/admissions/load_priority",data:{procedure:i},success:function(i){$("#priority_display").html(i)}})}),$("#admission_procedure_id, #admission_priority, #admission_listing_status, #admission_appointment_date").change(function(){var i=$("#admission_procedure_id").val(),a=$("#admission_priority").is(":checked"),s=$("#admission_listing_status").val(),o=$("#admission_appointment_date").val();$.ajax({url:"/admissions/load_submit",data:{procedure:i,priority:a,listing_status:s,appointment_date:o},success:function(i){$("#submit_display").html(i)}})})});